INSERT INTO `student_records` (`id`, `daily_record_id`, `daily_note`, `student_id`, `domain1`, `domain1_content`, `domain2`, `domain2_content`, `created_at`, `updated_at`) VALUES
(13, 4, '工作では、はさみの使い方が上手になってきました。集中して取り組む時間が長くなっています。', 5, 'motor_sensory', 'はさみを使って紙を切る動作がスムーズになりました。手先の器用さが向上しています。', 'cognitive_behavior', '作業の手順を理解して、最後まで集中して取り組むことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(14, 8, '算数の数の概念が少しずつ理解できてきています。10までの数を数えることができました。', 5, 'cognitive_behavior', '1から10までの数字を順番に数えることができました。', 'language_communication', '先生の指示を聞いて理解し、適切に行動できました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(15, 5, '体を動かすことが大好きで、積極的に参加していました。ボールを投げたり受け取ったりする動作が上手になっています。', 5, 'motor_sensory', 'ボールを両手でキャッチすることができました。体のバランス感覚が良くなっています。', 'social_relations', 'お友達と順番を守ってボール遊びができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(16, 9, 'けん玉に興味を持ち、何度も挑戦していました。うまくできなくても諦めない姿勢が素晴らしいです。', 5, 'motor_sensory', 'けん玉の練習を繰り返し、少しずつコツをつかんできました。', 'cognitive_behavior', '失敗しても諦めず、繰り返し挑戦する姿が見られました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(17, 6, 'ハートの形を丁寧に切り抜くことができました。色の組み合わせを考えて選ぶ姿が見られました。', 5, 'motor_sensory', 'はさみで曲線を切る技術が向上しました。細かい作業も丁寧にできています。', 'cognitive_behavior', '色の組み合わせを考えて選択する姿が見られ、計画性が育っています。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(18, 10, '楽器を使ってリズムを取ることができました。音楽に合わせて体を動かすのが楽しそうでした。', 5, 'motor_sensory', 'リズムに合わせて楽器を叩くことができました。', 'language_communication', '歌詞を覚えて一緒に歌うことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(19, 7, '公園で春の兆しを見つけて喜んでいました。梅の花を見つけて「きれいだね」と話していました。', 5, 'language_communication', '自分が見つけたものを言葉で表現して伝えることができました。', 'social_relations', 'お友達と一緒に探索活動を楽しむことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(20, 11, 'お友達と協力してパズルを完成させることができました。役割分担も自然にできていました。', 5, 'social_relations', 'お友達と協力して一つの目標を達成する喜びを感じていました。', 'cognitive_behavior', 'パズルのピースの形を見て、正しい場所を見つけることができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(21, 12, 'ひな人形の並べ方を覚えて、正しい位置に飾ることができました。日本の伝統文化に興味を持っています。', 5, 'cognitive_behavior', 'ひな人形の正しい配置を覚えて、順番に並べることができました。', 'language_communication', 'ひな祭りの歌を元気に歌い、行事の意味を理解していました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(22, 17, 'ひらがなの読みが上達しています。自分の名前を書くことができるようになりました。', 5, 'language_communication', 'ひらがなを一つずつ丁寧に書く練習ができました。', 'cognitive_behavior', '文字の形を正確に認識し、真似して書くことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(23, 13, '桜の花びらを折り紙で作りました。指先を使った細かい作業が得意になってきています。', 5, 'motor_sensory', '折り紙を丁寧に折る動作ができました。指先の力加減が上手になっています。', 'cognitive_behavior', '手順を見ながら、順番通りに作業を進めることができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(24, 18, '春の歌「チューリップ」を覚えて歌っていました。メロディーに合わせて手遊びもできました。', 5, 'language_communication', '歌詞を覚えて、はっきりとした声で歌うことができました。', 'motor_sensory', 'リズムに合わせて手や体を動かすことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(25, 14, '縄跳びの練習で、連続で5回跳べるようになりました。諦めずに練習する姿が素晴らしいです。', 5, 'motor_sensory', '縄跳びで連続して跳ぶことができるようになりました。', 'cognitive_behavior', '目標を設定して、達成するまで繰り返し練習する姿が見られました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(26, 19, '絵の具の混色を楽しんでいました。「青と黄色で緑になった！」と発見を喜んでいました。', 5, 'cognitive_behavior', '色の変化を観察し、因果関係を理解していました。', 'language_communication', '自分の発見を言葉で表現し、喜びを共有できました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(27, 15, '満開の桜を見て「きれいだね」「春だね」と季節の変化を感じていました。お花見を楽しんでいました。', 5, 'language_communication', '季節の変化を言葉で表現することができました。', 'social_relations', 'お友達と一緒に桜を見て、感動を共有することができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(28, 20, 'ゲームのルールを理解して、順番を守って参加できました。負けても笑顔で「次は頑張る！」と前向きでした。', 5, 'social_relations', 'ルールを守り、お友達と楽しく遊ぶことができました。', 'cognitive_behavior', 'ゲームのルールを理解し、適切に行動できました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(29, 16, '1年間の成長を振り返りました。「できるようになったことがたくさんある」と自信を持って話していました。', 5, 'language_communication', '自分の成長を言葉で表現し、振り返ることができました。', 'cognitive_behavior', '過去と現在を比較して、自分の成長を認識できました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(30, 25, 'エイプリルフールのサプライズを楽しんでいました。冗談を理解して笑う姿が見られました。', 5, 'social_relations', 'ユーモアを理解し、お友達と笑い合うことができました。', 'language_communication', '冗談や比喩的な表現を理解できるようになってきました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(31, 21, '新学年の目標を「もっと本を読めるようになりたい」と設定しました。前向きな姿勢が素晴らしいです。', 5, 'language_communication', '自分の目標を明確に言葉で表現できました。', 'cognitive_behavior', '将来の計画を立て、目標を設定する力が育っています。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(32, 26, '個別学習で集中力が高まっています。20分間継続して学習に取り組めました。', 5, 'cognitive_behavior', '集中して学習に取り組む時間が長くなりました。', 'health_life', '正しい姿勢で学習に取り組むことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(33, 22, '遠足の準備を通して、持ち物を自分で確認する習慣がついてきました。', 5, 'health_life', '必要な持ち物を自分でチェックリストを見ながら確認できました。', 'cognitive_behavior', '計画的に準備を進めることができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(34, 27, 'カラフルなこいのぼりを作りました。色の塗り方が丁寧で、集中して取り組んでいました。', 5, 'motor_sensory', 'クレヨンで線の内側を丁寧に塗ることができました。', 'cognitive_behavior', '作品を完成させるまで集中力を保つことができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(35, 23, '体幹トレーニングに積極的に取り組んでいます。バランスボールの上に座ることができました。', 5, 'motor_sensory', 'バランス感覚が向上し、体幹が強くなってきています。', 'cognitive_behavior', '体の使い方を考えながら運動に取り組めました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(36, 28, 'お友達に「一緒に遊ぼう」と自分から声をかけることができました。コミュニケーションが積極的になっています。', 5, 'language_communication', '自分から相手に話しかけることができました。', 'social_relations', '友達との関わりを自分から求める姿が見られました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(37, 24, 'チーム対抗ゲームで、仲間を応援する姿が見られました。協力することの大切さを学んでいます。', 5, 'social_relations', 'チームメイトを応援し、協力して活動できました。', 'language_communication', '「頑張って！」など励ましの言葉をかけることができました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(38, 29, 'けん玉が上達し、3回連続で成功しました。練習の成果が出ています。', 5, 'motor_sensory', 'けん玉の動作が安定し、成功率が上がりました。', 'cognitive_behavior', '成功体験を通して自信をつけています。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(39, 30, 'こいのぼりを飾り、こどもの日をお祝いしました。日本の行事に親しんでいます。', 5, 'language_communication', 'こどもの日の由来について学び、理解を深めました。', 'social_relations', '行事を通して、文化を大切にする気持ちを育んでいます。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
(40, 34, '連休明けでしたが、元気に活動に参加できました。気持ちの切り替えが上手になっています。', 5, 'health_life', '休み明けでも生活リズムを保つことができました。', 'cognitive_behavior', '気持ちを切り替えて活動に集中できました。', '2025-10-17 15:22:05', '2025-10-17 15:22:05'),
